import React from 'react'
import {fetchPlayerData} from '../adaptors/PlayerAdaptor';
import {useState, useEffect} from 'react';
import Button from '../components/Button';
import {postAllPlayers, updateAllPlayers} from '../adaptors/BackendAdapter'

function AdminPage() {



    const[players, setPlayers] = useState([]);

    useEffect(() => {
        fetchAllLeagueData()}, [])


    const fetchAllLeagueData = () => {

        fetchAllPagesBundesliga()
        fetchAllPagesLaLiga();
        fetchAllPagesLigue1();
        fetchAllPagesPremierLeague();
        fetchAllPagesSerieA();

        alert("Done");

    }


    const fetchAllPagesLigue1 = () => {

        const league = 61;
        const pages = 40;

        for(let i = 1; i <= pages; i++) {
            fetchPlayerData(league, i)
            .then(response => setPlayers([...players, ...response]))
        }

    } 

    const fetchAllPagesPremierLeague = () => {

        const league = 39;
        const pages = 36;

        for(let i = 1; i <= pages; i++) {
            fetchPlayerData(league, i)
            .then(response => setPlayers([...players, ...response]))
        }

    } 

    const fetchAllPagesBundesliga = () => {

        const league = 78;
        const pages = 33;

        for(let i = 1; i <= pages; i++) {
            fetchPlayerData(league, i)
            .then(response => {
            setPlayers([...players, ...response]);
            console.log(players)})
        }

    } 

    const fetchAllPagesSerieA = () => {

        const league = 135;
        const pages = 44;

        for(let i = 1; i <= pages; i++) {
            fetchPlayerData(league, i)
            .then(response => setPlayers([...players, ...response]))
        }

    } 

    const fetchAllPagesLaLiga = () => {

        const league = 140;
        const pages = 35;

        for(let i = 1; i <= pages; i++) {
            fetchPlayerData(league, i)
            .then(response => setPlayers(players.push(response)))
        }

    } 




    const postPlayersToDb = () => {
        postAllPlayers(players)
    }

    const updatePlayersInDb = () => {
        updateAllPlayers(players)
    }

    console.log(players);

    return (

        players.length > 0 ?


        <div>
            <Button clickMethod={postPlayersToDb} name="Post players to db" />
            <Button clickMethod={updateAllPlayers} name="Update player stats" />
           {players[0].name}
        </div>

        : 
        <div>loading...</div>
    )
}

export default AdminPage
